document.addEventListener('DOMContentLoaded', function() { const cookieBox = document.getElementById('cookie-consent-box'); const acceptBtn = cookieBox.querySelector('button:first-child'); const closeBtn = cookieBox.querySelector('button:last-child'); if (!document.cookie.includes('cookie_consent=true')) { cookieBox.style.display = 'flex'; } acceptBtn.addEventListener('click', function() { document.cookie = 'cookie_consent=true; max-age=2592000; path=/'; cookieBox.style.display = 'none'; }); closeBtn.addEventListener('click', function() { cookieBox.style.display = 'none'; }); });